import { useCallback } from "react";
import Input from "./Input";
interface LoginProps {
  setRegister: (value: boolean) => void;
}
const handleSubmit = (e: any) => {
  e.preventDefault();
  const { email, password } = e.target.elements;
  console.log(email.value, password.value);
};

const handleRegister = useCallback(() => {
  setRegister(true);
}, []);

const Login = () => {
  return (
    <section className=" bg-blue-950 w-screen h-screen flex justify-center items-center">
      <div className="w-full md:w-3/5 xl:w-2/5 h-3/5  bg-white rounded-xl shadow-2xl shadow-black flex flex-col items-center justify-center">
        <h1 className="text-6xl ">Login</h1>
        <form
          className="flex justify-center flex-col"
          onSubmit={handleSubmit}
          method="post"
        >
          <Input name="email" type="email" label="Email" />
          <Input type="password" name="password" label="Password" />
          <div className="flex justify-between px-3">
            <p className="text-md">Don't remember a password?</p>
            <p className="underline cursor-pointer hover:text-green-600">
              Click here
            </p>
          </div>
          <button
            type="submit"
            className="bg-green-700 text-white p-3 m-3 hover:bg-green-600 duration-500 transition-all font-medium"
          >
            Continue
          </button>
        </form>
      </div>
    </section>
  );
};

export default Login;
