import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setCredentials } from "../state/user";

const ToDo = () => {
  const user = useSelector((state: any) => state.user.token);
  const dispatch = useDispatch();

  const showUser = async () => {
    const response = await fetch("http://localhost:5000/verify", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${user}`,
      },
    });
    if (!response.ok) {
      const refreshResponse = await fetch("http://localhost:5000/refresh", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${user}`,
        },
      });
      if (refreshResponse.ok) {
        const data = await refreshResponse.json();
        dispatch(setCredentials(data));
      } else {
        dispatch(setCredentials(null));
      }
    }
  };

  useEffect(() => {
    showUser();
    const intervalId = setInterval(showUser, 30000);
    return () => clearInterval(intervalId);
  }, []);

  return user ? <div>You're here.</div> : <div>You're not here.</div>;
};

export default ToDo;
