import React, { useState } from "react";
interface InputProps {
  type?: string;
  placeholder?: string;
  label?: string;
  id: string;
}

const Input: React.FC<InputProps> = ({ type, placeholder, label, id }) => {
  const [isFocused, setIsFocused] = useState<boolean>(false);
  return (
    <div className=" flex flex-row relative">
      <label htmlFor={id} className="absolute left-4 bg-white">
        {label}
      </label>

      <input
        id={type}
        className="p-3 border focus:outline-none border-black placeholder:text-gray-400 m-3 "
        type={type}
      />
    </div>
  );
};

export default Input;
