import React, { useState } from "react";
interface InputProps {
  type?: string;
  placeholder?: string;
  label?: string;
}

const Input: React.FC<InputProps> = ({ type, placeholder, label }) => {
  const [isFocused, setFocused] = useState<boolean>(false);
  return (
    <div className="w-3/4 flex flex-col relative items-center">
      <label
        htmlFor={type}
        className={`absolute text-left flex text-black/50 transform ${
          isFocused
            ? "translate-y-0 scale-50 translate-x-0"
            : "translate-y-1/2 "
        } left-14 cursor-text transition-transform`}
      >
        {label}
      </label>
      <input
        id={type}
        className="p-3 w-3/4 border-2 focus:outline-none border-b-2 border-green-600 placeholder:text-gray-400"
        type={type}
        onFocus={() => setFocused(true)}
        onBlur={() => setFocused(false)}
      />
    </div>
  );
};

export default Input;
