import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setCredentials, updateToken } from "../state/user";

const ToDo = () => {
  const token = useSelector((state: any) => state.user.token);
  const dispatch = useDispatch();

  const showUser = async () => {
    const response = await fetch("http://localhost:5000/verify", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
    });
    if (!response.ok) {
      const refreshResponse = await fetch("http://localhost:5000/refresh", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        credentials: "include",
      });
      if (refreshResponse.ok) {
        const data = await refreshResponse.json();
        dispatch(updateToken({ token: data.token }));
      } else {
        dispatch(setCredentials({ token: "", email: "", username: "" }));
      }
      //console.log(user);
    }
  };

  useEffect(() => {
    showUser();
    const intervalId = setInterval(showUser, 10000);
    return () => clearInterval(intervalId);
  }, []);

  return user ? <div>You're here.</div> : <div>You're not here.</div>;
};

export default ToDo;
